import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import styled from 'styled-components'; // styled-components import
import Bar from '../components/Bars';
import HeartToggle from '../components/HeartToggle';
import WritePostButton from '../components/WritePostButton'; // 버튼 컴포넌트 import

const PageContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between; 
    height: 100vh; 
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: center; 
    padding: 35px; 
`;

const Mainpage = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:8001/api/data')
            .then(response => {
                setData(response.data.message);
            })
            .catch(error => {
                console.error('There was an error fetching the data!', error);
            });
    }, []);

    return (
        <PageContainer>
            <Bar />
            <HeartToggle />
            <p>{data ? data : "No data available"}</p>
            <Link to="/detailpage" style={{ textDecoration: 'none', color: 'black', fontSize: '16px' }}>
                Go to Detail Page
            </Link>
            <ButtonContainer>
                <WritePostButton /> {/* 글쓰기 버튼 추가 */}
            </ButtonContainer>
        </PageContainer>
    );
}

export default Mainpage;
